$sql_data_tindakan = "SELECT rt.id_poli, rt.kode_penyakit,rt.kode_tindakan,rt.no_rawat,rt.hasil_periksa,rt.nama_obat,rt.tanggal,p.nama_poli, pkt.nama_penyakit, t.nama_tindakan, pd.no_rekamedis FROM tbl_riwayat_tindakan as rt, tbl_tindakan as t, tbl_poli as p, tbl_diagnosa_penyakit as pkt, tbl_pasien as pas, tbl_pendaftaran as pd WHERE rt.no_rekamedis = pd.no_rekamedis AND rt.kode_tindakan = t.kode_tindakan AND rt.id_poli = p.id_poli AND rt.kode_penyakit = pkt.kode_diagnosa AND pd.no_rawat = '$no_rawat'";